using System; using OOP_Course_Work.DeliveryType;  namespace OOP_Course_Work.OrderType {     class OrderSelector     {         public IOrderType Select(float weight)         {             if (weight <= 5)             {                 return new SmallOrder();             }             else if (weight > 5 && weight < 10)             {                 return new MeduimOrder();             }             else return new LargeOrder();         }     } } 